import React, { useEffect, useState} from 'react';
import axios from 'axios';
import http from '../http-common';
import { Box, TextField } from '@mui/material';


const CadastrarProposta = () => {
    const [loading, setLoading] = useState(true);
    const [campos, setCampos] = useState([])
    const hashMap = new Map();
    const [formValues, setFormValues] = useState({

    })


    useEffect(() => {
        setLoading(true)
        http.get('/campos')
        .then(response => {

          setCampos(response.data)
        })

        .then(setLoading(false))

        .catch(error =>{
          console.log(error)
          setLoading(false)
        })

    }, [loading]);
    const handleInputChange = (e, id) => {
      const { value } = e.target;
      setFormValues({ ...formValues, [id]: value });
      console.log(formValues)
    };
    const handleSubmit = (event) => {
      event.preventDefault();
      setLoading(true)

      const jsonData = JSON.stringify(formValues);
      http.post('/')

      axios.post('https://exemplo.com/api/dados', jsonData)
        .then(response => {
          // Lógica de manipulação da resposta da API
        })
        .catch(error => {
          // Lógica de tratamento de erros
        });
    };
  if(loading){
    return <p>Carregando..</p>
  }
  if (campos.length === 0) {
    return <p>Não há campos disponíveis para cadastro de propostas</p>
  }
  return (

    <div>

        <h2>Formulário de proposta</h2>


        <Box sx={{maxWidth: '100%'}}>
        {campos.map((i, index)=>(

          <div>
            <TextField 
            onChange={(e)=>handleInputChange(e, i.slug)}
            fullWidth 
            label={i.nome} 
            id={i.slug}
            required={i.obrigatorio}
            helperText={i.texto_ajuda}
            margin="normal" />
          </div>
        ))}

        </Box>
      </div>
  )
}

export default CadastrarProposta;
